import React, { useState } from 'react';
import axios from 'axios';

function WordGuessGame() {
  const [wordLength, setWordLength] = useState(5);
  const [computerWord, setComputerWord] = useState('');
  const [userGuess, setUserGuess] = useState('');
  const [matchingLetters, setMatchingLetters] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  const startNewGame = () => {
    // Make an API request to generate a random word with the specified length
    if (wordLength > 0) {
      axios.get('https://random-word-api.herokuapp.com/word', {
        params: {
          length: wordLength
        }
      })
      .then(response => {
        setComputerWord(response.data[0]);
        setMatchingLetters(0);
        setGameOver(false);
      })
      .catch(error => {
        console.error(error);
      });
    }
  };

  const handleGuess = () => {
    // Make an API request to compare the user's guess with the computer's word
    axios.post('http://localhost:8080/api/word/compare', {
      userGuess: userGuess,
      computerWord: computerWord
    })
    .then(response => {
      setMatchingLetters(response.data);
      if (userGuess === computerWord) {
        setGameOver(true);
      }
    })
    .catch(error => {
      console.error(error);
    });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Word Guessing Game</h1>
      <div className="row justify-content-center mt-4">
        <div className="col-md-6">
          <div className="form-group">
            <label htmlFor="wordLength">Enter word length:</label>
            <input
              type="number"
              id="wordLength"
              className="form-control"
              value={wordLength}
              onChange={(e) => setWordLength(e.target.value)}
            />
          </div>
          <button className="btn btn-primary" onClick={startNewGame}>
            Start New Game
          </button>
        </div>
      </div>
      {gameOver ? (
        <div className="row justify-content-center mt-4">
          <div className="col-md-6 text-center">
            <h2>Congratulations! You guessed the word.</h2>
          </div>
        </div>
      ) : (
        <div className="row justify-content-center mt-4">
          <div className="col-md-6">
            <div className="form-group">
              <label htmlFor="userGuess">Enter your guess:</label>
              <input
                type="text"
                id="userGuess"
                className="form-control"
                value={userGuess}
                onChange={(e) => setUserGuess(e.target.value)}
              />
            </div>
            <button className="btn btn-primary" onClick={handleGuess}>
              Guess
            </button>
          </div>
          <div className="col-md-6 text-center mt-3">
            <p>Matching Letters: {matchingLetters}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default WordGuessGame;
