import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WordGuessGame() {
  const [wordLength, setWordLength] = useState(5);
  const [computerWord, setComputerWord] = useState('');
  const [userGuess, setUserGuess] = useState('');
  const [matchingLetters, setMatchingLetters] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    // Make an API request to generate a random word with the specified length
    if (wordLength > 0) {
      axios.get(`/api/word/generate?wordLength=${wordLength}`)
        .then(response => {
          setComputerWord(response.data);
          setMatchingLetters(0);
          setGameOver(false);
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [wordLength]);

  const handleGuess = () => {
    // Make an API request to compare the user's guess with the computer's word
    axios.post('/api/word/compare', {
      userGuess: userGuess,
      computerWord: computerWord
    })
    .then(response => {
      setMatchingLetters(response.data);
      if (userGuess === computerWord) {
        setGameOver(true);
      }
    })
    .catch(error => {
      console.error(error);
    });
  };

  return (
    <div className="WordGuessGame">
      <h1>Word Guessing Game</h1>
      <div>
        <label>Enter word length: </label>
        <input type="number" value={wordLength} onChange={(e) => setWordLength(e.target.value)} />
        <button onClick={() => setWordLength(wordLength)}>Start New Game</button>
      </div>
      {gameOver ? (
        <div>
          <h2>Congratulations! You guessed the word.</h2>
        </div>
      ) : (
        <div>
          <div>
            <label>Enter your guess: </label>
            <input type="text" value={userGuess} onChange={(e) => setUserGuess(e.target.value)} />
            <button onClick={handleGuess}>Guess</button>
          </div>
          <div>Matching Letters: {matchingLetters}</div>
        </div>
      )}
    </div>
  );
}

export default WordGuessGame;
